void MoveZeros(vector<int>& vals) 
{
	vector<int>::iterator it1 = vals.begin();
	vector<int>::iterator it2 = vals.begin();

	while (it2 != vals.end())
	{
		if (*it2 != 0)
		{
			*it1 = *it2;
			++it1;
		}
		++it2;
	}

	while (it1 != vals.end())
	{
		*it1 = 0;
		++it1;
	}
}

